package day2;

import java.util.List;

import org.junit.Test;

import static junit.framework.Assert.assertEquals;
import static org.hamcrest.CoreMatchers.is;
import static org.hamcrest.MatcherAssert.assertThat;

public class Puzzle2 {

    String input = "\n" +
            "\n" +
            "\n" +
            "\n" +
            "";


    @Test
            public void shouldCalculateExample() {
        Keypad keypad = new Keypad();
String input = "ULL\n" +
        "RRDDD\n" +
        "LURDL\n" +
        "UUUUD";

        List<Integer> press = keypad.press(input);

        assertThat(press.get(0), is(1));
        assertThat(press.get(1), is(9));
        assertThat(press.get(2), is(8));
        assertThat(press.get(3), is(5));
    }

    @Test
    public void calculateInput() {
        Keypad keypad = new Keypad();

        List<Integer> press = keypad.press(input);
        System.out.println(press);
    }

    @Test
    public void calculateExample() {
        FunkyKeypad keypad = new FunkyKeypad();
        String input = "ULL\n" +
                "RRDDD\n" +
                "LURDL\n" +
                "UUUUD";

        List<Character> press = keypad.press(input);

        assertEquals(press.get(0).charValue(), '5');
        assertEquals(press.get(1).charValue(), 'D');
        assertEquals(press.get(2).charValue(), 'B');
        assertEquals(press.get(3).charValue(), '3');
    }



    @Test
    public void calculateFunkyExample() {
        FunkyKeypad keypad = new FunkyKeypad();
        List<Character> press = keypad.press(input);
        System.out.println(press);
    }

}
